sp_header_footer.client_script:
api.controller = function() {
    const c = this;

    // Load the menu
    c.server.get({
        action: 'loadMenu'
    }).then((response) => {
        c.menuWidget = response.data.menu;
    });

    // Get the user initials
    c.server.get({
        action: 'getUserInitials'
    }).then((response) => {
        c.userInitials = response.data.userInitials;
    });
};

